import React, {useState} from 'react'
import { evaluate } from 'mathjs'

export default function Calculator() {

    const operators = {
        multiply: '*',
        divide: '/'
    }

    const [numbers, setNumbers] = useState([
        {
            num: '0',
            rightOp: null,
            deci: false,
            isNeg: false
        }
    ])



    return (
        <div id="calculator">
            <div id="display-container">
                <p id="full-exp">{fullExp}</p>
                <p id="display">{dispText}</p>
            </div>

            <div id="buttons-container">
                <button id="clear" onClick={handleClear}>AC</button>
                <button id="divide" onClick={handleOpPress} value='/'>/</button>
                <button id="multiply" onClick={handleOpPress} value='*'>x</button>
                <button id="add" onClick={handleOpPress} value='+'>+</button>
                <button id="subtract" onClick={handleSubtract} value='-'>-</button>
                <button id="equals" onClick={calc}>=</button>

                <button id="zero" value={'0'} onClick={handleNumPress}>0</button>
                <button id="one" value={'1'} onClick={handleNumPress}>1</button>
                <button id="two" value={'2'} onClick={handleNumPress}>2</button>
                <button id="three" value={'3'} onClick={handleNumPress}>3</button>
                <button id="four" value={'4'} onClick={handleNumPress}>4</button>
                <button id="five" value={'5'} onClick={handleNumPress}>5</button>
                <button id="six" value={'6'} onClick={handleNumPress}>6</button>
                <button id="seven" value={'7'} onClick={handleNumPress}>7</button>
                <button id="eight" value={'8'} onClick={handleNumPress}>8</button>
                <button id="nine" value={'9'} onClick={handleNumPress}>9</button>

                <button id="decimal" value={'.'} onClick={handleNumPress}>.</button>
            </div>
        </div>
    )
}


