import React, {useState} from 'react'
import { evaluate } from 'mathjs'

export default function Calculator() {
    const [currentNum, setCurrentNum] = useState('0');
    const [fullExp, setFullExp] = useState('');
    const [dispText, setDispText] = useState(currentNum)
    const [currentInd, setCurrentInd] = useState(0);
    const [isNegative, setIsNegative] = useState(false)
    //const [result, setResult] = useState(0);

    function testNotOp(sym) {
        return (sym === 'x' || sym === '/' || sym === '+')
    }

    function handleNumPress(event) {
        let val = event.target.value;

        const deciReg = /\./
        let updatedNum

        if (currentNum.length === 1 && currentNum === '0') {
            if (val === '.') {
                updatedNum = currentNum + val;
            } else if (val !== '0') {
                updatedNum = val;
            } else {
                updatedNum = '0'
            }
        } else if (val === '.') {
            if (!deciReg.test(currentNum)) {
                updatedNum = currentNum + val;
            } 
        } else {
            updatedNum = currentNum + val;
        }

        if (updatedNum) {
            if (isNegative) {
                setDispText('-' + updatedNum)
            } else {
                setDispText(updatedNum)
            }
            setCurrentNum(updatedNum)
            setFullExp(fullExp.slice(0, currentInd) + updatedNum)
        }
        
    }

    function handleClear() {
        setCurrentNum('0');
        setFullExp('');
        setDispText('0')
        setCurrentInd(0)
    }

    function handleOpPress(event) {
        let val = event.target.value;
        
        setCurrentNum('0')
        setDispText(val)
        setCurrentInd(fullExp.length+1)
        setIsNegative(false)

        let prevOp = testNotOp(fullExp.slice(fullExp.length-1))
                 
        if (prevOp) {
            setFullExp(fullExp.slice(0, fullExp.length-1) + val)
        } else {
            setFullExp(fullExp + val)
        }  
    }

    function handleSubtract() {
        let val = '-'
        setCurrentNum('0')
        setDispText(val)
        setCurrentInd(fullExp.length+1)
        setIsNegative(false)
        
        let prevOp = testNotOp(fullExp.slice(fullExp.length-1))
        let prev2Op = testNotOp(fullExp.slice(fullExp.length-2, fullExp.length-1))

        let addMinus
        let len = fullExp.length

        if (fullExp.slice(len-1) == '-') {
            if (!prev2Op && fullExp.slice(len-2, len-1) !== '-') {
                addMinus = true
                setIsNegative(true)
            }
        } else if (prevOp) {
            addMinus = true
                setIsNegative(true)
        } else {
            addMinus = true
        }

        if (addMinus) {
            setFullExp(fullExp + val)
        }
    }

    function calc() {
        let result = evaluate(fullExp).toString()
        
        setDispText(result)
        setCurrentNum(result)
        setCurrentInd(fullExp.length+1)
        setFullExp(result)
        setIsNegative(false)
    }

    return (
        <div id="calculator">
            <div id="display-container">
                <p id="full-exp">{fullExp}</p>
                <p id="display">{dispText}</p>
            </div>

            <div id="buttons-container">
                <button id="clear" onClick={handleClear}>AC</button>
                <button id="divide" onClick={handleOpPress} value='/'>/</button>
                <button id="multiply" onClick={handleOpPress} value='*'>x</button>
                <button id="add" onClick={handleOpPress} value='+'>+</button>
                <button id="subtract" onClick={handleSubtract} value='-'>-</button>
                <button id="equals" onClick={calc}>=</button>

                <button id="zero" value={'0'} onClick={handleNumPress}>0</button>
                <button id="one" value={'1'} onClick={handleNumPress}>1</button>
                <button id="two" value={'2'} onClick={handleNumPress}>2</button>
                <button id="three" value={'3'} onClick={handleNumPress}>3</button>
                <button id="four" value={'4'} onClick={handleNumPress}>4</button>
                <button id="five" value={'5'} onClick={handleNumPress}>5</button>
                <button id="six" value={'6'} onClick={handleNumPress}>6</button>
                <button id="seven" value={'7'} onClick={handleNumPress}>7</button>
                <button id="eight" value={'8'} onClick={handleNumPress}>8</button>
                <button id="nine" value={'9'} onClick={handleNumPress}>9</button>

                <button id="decimal" value={'.'} onClick={handleNumPress}>.</button>
            </div>
        </div>
    )
}


