import React, {useEffect, useState} from 'react'
import { evaluate } from 'mathjs'

const iniNumState = {
    num: '',
    rightOp: null,
    deci: false,
    isNeg: false
};

const numbers = [iniNumState]

const buildExp = (numArr) => {
    let simplified = numArr.slice(1).map(x => {
        if (x.isNeg) {
            return '-' + x.num + x.rightOp
        } else {
            return x.num + x.rightOp
        }
    })
    return simplified.join('')
}

export default function Calculator() {
    const [numObj, setNumObj] = useState(iniNumState)
    const [currentInd, setCurrentInd] = useState(0)
    const [dispText, setDispText] = useState('0')
    const [lastInput, setLastInput] = useState(null)

    function buildNumber(event) {
        let currentNum;
        let val = event.target.value;

        if (lastInput === 'OPERATOR') {
            currentNum = {...iniNumState};
            numbers.push(numObj)
        } else {
            currentNum = {...numObj}
        }

        if (val === '.') {
            if (!currentNum.deci) {
                if (currentNum.num === '') {
                    currentNum.num = '0.'
                    currentNum.deci = true;
                    setNumObj(currentNum)
                } else {
                    currentNum.num = currentNum.num + val;
                    currentNum.deci = true;
                    setNumObj(currentNum)
                }
            }
        } else if (val === '-') {
            if (!currentNum.isNeg) {
                if (currentNum.num === '0') {
                    currentNum.num = '';
                    currentNum.isNeg = true;
                    setNumObj(currentNum)
                } else {
                    currentNum.isNeg = true
                    setNumObj(currentNum)
                }
            }
        } else if (currentNum.num === '0') {
            if (val !== '0') {
                currentNum.num = val
                setNumObj(currentNum)
            } else {
                currentNum.num = '0'
                setNumObj(currentNum)
            }
        } else {
            currentNum.num = currentNum.num + val
            setNumObj(currentNum)
        }

        if (currentNum.isNeg) {
            setDispText('-' + currentNum.num)
        } else {
            setDispText(currentNum.num)
        }

        setLastInput('NUMBER')
    }

    function handleOperator(event) {
        let val = event.target.value;
        let currentNum = {...numObj}

        if (val === '-') {
            if (currentNum.rightOp !== null) {
                buildNumber(event)
            } else if (currentNum.num === '') {
                buildNumber(event)
            } else {
                setLastInput('OPERATOR')
                currentNum.rightOp = val;
                setNumObj(currentNum);
                setDispText(val)
            }
        } else {
            setLastInput('OPERATOR')
            currentNum.rightOp = val;
            setNumObj(currentNum);
            setDispText(val)
        }
    }

    function clr() {
        setNumObj(iniNumState)
        setCurrentInd(0)
        setDispText('0')
        setLastInput(null)
        setDispText('0')
    }

    function calc() {
        // Prioritize x and /
        console.log(numbers)
        console.log(numbers.map(x => x.rightOp === '*' || x.rightOp === '/'))
        console.log(numbers.map(x => x.rightOp === '*' || x.rightOp === '/').indexOf(true))

        let numArr = numbers.slice(1)

        function isMD(arr) {
            return arr.map(x => x.rightOp === '*' || x.rightOp === '/')
        }

        let indMD
        while (isMD(numArr).indexOf(true) >= 0) {
            isMD(numArr).indexOf(true) 
        }

    }

    return (
        <div id="calculator">
            <div id="display-container">
                <p id="full-exp">test</p>
                <p id="display">{dispText}</p>
            </div>

            <div id="buttons-container">
                <button id="clear" onClick={clr}>AC</button>
                <button id="divide" onClick={handleOperator} value='/'>/</button>
                <button id="multiply" onClick={handleOperator} value='*'>x</button>
                <button id="add" onClick={handleOperator} value='+'>+</button>
                <button id="subtract" onClick={handleOperator} value='-'>-</button>
                <button id="equals" onClick={calc}>=</button>

                <button id="zero" value={'0'} onClick={buildNumber}>0</button>
                <button id="one" value={'1'} onClick={buildNumber}>1</button>
                <button id="two" value={'2'} onClick={buildNumber}>2</button>
                <button id="three" value={'3'} onClick={buildNumber}>3</button>
                <button id="four" value={'4'} onClick={buildNumber}>4</button>
                <button id="five" value={'5'} onClick={buildNumber}>5</button>
                <button id="six" value={'6'} onClick={buildNumber}>6</button>
                <button id="seven" value={'7'} onClick={buildNumber}>7</button>
                <button id="eight" value={'8'} onClick={buildNumber}>8</button>
                <button id="nine" value={'9'} onClick={buildNumber}>9</button>

                <button id="decimal" value={'.'} onClick={buildNumber}>.</button>
            </div>
        </div>
    )
}


