import React, {useEffect, useState} from 'react'

const deciRegex = /\./
const negRegex = /^-/

const iniNumState = {
    num: '',
    nextOp: null,
    deci: false,
    isNeg: false
};

var numbers = [iniNumState]

const buildExp = (numArr) => {
    let simplified = numArr.slice(1).map(x => {
        if (x.isNeg) {
            return '-' + x.num + x.rightOp
        } else {
            return x.num + x.rightOp
        }
    })
    return simplified.join('')
}

const resolveNeg = (numArr) => {
    return numArr.map(x => {
        if (x.isNeg) {
            return {
                ...x,
                num: '-' + x.num
            }
        } else {
            return x
        }
    })
}


export default function Calculator() {
    const [numObj, setNumObj] = useState(iniNumState)
    const [dispText, setDispText] = useState('0')
    const [lastInput, setLastInput] = useState(null)
    const [fullExp, setFullExp] = useState('')


    // Build current number
    function buildNumber(event) {
        let currentNum;
        let val = event.target.value;

        if (lastInput === 'OPERATOR') {
            currentNum = {...iniNumState};
            numbers.push(numObj)
        } else if (lastInput === 'EQUALS') {
            currentNum = {...iniNumState};
            numbers = [iniNumState]
        } else {
            currentNum = {...numObj}
        }

        if (val === '.') {
            if (!currentNum.deci) {
                if (currentNum.num === '') {
                    currentNum.num = '0.'
                    currentNum.deci = true;
                    setNumObj(currentNum)
                } else {
                    currentNum.num = currentNum.num + val;
                    currentNum.deci = true;
                    setNumObj(currentNum)
                }
            }
        } else if (val === '-') {
            if (!currentNum.isNeg) {
                if (currentNum.num === '0') {
                    currentNum.num = '';
                    currentNum.isNeg = true;
                    setNumObj(currentNum)
                } else {
                    currentNum.isNeg = true
                    setNumObj(currentNum)
                }
            }
        } else if (currentNum.num === '0') {
            if (val !== '0') {
                currentNum.num = val
                setNumObj(currentNum)
            } else {
                currentNum.num = '0'
                setNumObj(currentNum)
            }
        } else {
            currentNum.num = currentNum.num + val
            setNumObj(currentNum)
        }


        currentNum.isNeg ? setDispText('-' + currentNum.num) : setDispText(currentNum.num)

        setLastInput('NUMBER')

        setFullExp(buildExp([...numbers, currentNum]))
    }

    // Assign operator
    function handleOperator(event) {
        let val = event.target.value;
        let currentNum = {...numObj}

        if (val === '-') {
            if (currentNum.nextOp !== null) {
                buildNumber(event)
            } else if (currentNum.num === '') {
                buildNumber(event)
            } else {
                setLastInput('OPERATOR')
                currentNum.nextOp = val;
                setNumObj(currentNum);
                setDispText(val)
            }
        } else {
            setLastInput('OPERATOR')
            currentNum.nextOp = val;
            setNumObj(currentNum);
            setDispText(val)
        }
        
        // Correct false negative number if neccessary
        if (currentNum.num === '') {
            numbers[numbers.length - 1].nextOp = val;
            setNumObj(numbers[numbers.length - 1])
            numbers.pop()
        }
    }

    function clr() {
        setNumObj(iniNumState)
        setDispText('0')
        setLastInput(null)
        setDispText('0')
        numbers = [iniNumState]
    }

    function calc() {
        numbers.push(numObj)
        numbers = resolveNeg(numbers)
        let numArr = numbers.slice(1)

        function isMD(arr) {
            return arr.map(x => x.nextOp === '*' || x.nextOp === '/')
        }

        let indMD;
        let res;

        while(isMD(numArr).indexOf(true) >= 0) {
            indMD = isMD(numArr).indexOf(true)
            
            if (numArr[indMD].nextOp === '*') {
                res = Number(numArr[indMD].num) * Number(numArr[indMD + 1].num)
            } else if (numArr[indMD].nextOp === '/') {
                res = Number(numArr[indMD].num) / Number(numArr[indMD + 1].num)
            }

            let substitute = {
                num: res,
                nextOp:  numArr[indMD + 1].nextOp,
                deci: deciRegex.test(res),
                isNeg: negRegex.test(res)
            }

            numArr = [...numArr.slice(0, indMD), substitute, ...numArr.slice(indMD+2)]
        }

        while(numArr.length > 1) {
            if (numArr[0].nextOp === '+') {
                res = Number(numArr[0].num) + Number(numArr[1].num)
            } else if (numArr[0].nextOp === '-') {
                res = Number(numArr[0].num) - Number(numArr[1].num)
            }

            let substitute = {
                num: res,
                nextOp:  numArr[1].nextOp,
                deci: deciRegex.test(res),
                isNeg: negRegex.test(res)
            }

            numArr = [substitute, ...numArr.slice(2)]
        }

        setNumObj(...numArr)
        setDispText(numArr[0].num)
        setLastInput('EQUALS')
        numbers = [...numArr]
    }

    return (
        <div id="calculator">
            <div id="display-container">
                <p id="full-exp">{fullExp}</p>
                <p id="display">{dispText}</p>
            </div>

            <div id="buttons-container">
                <button id="clear" onClick={clr}>AC</button>
                <button id="divide" onClick={handleOperator} value='/'>/</button>
                <button id="multiply" onClick={handleOperator} value='*'>x</button>
                <button id="add" onClick={handleOperator} value='+'>+</button>
                <button id="subtract" onClick={handleOperator} value='-'>-</button>
                <button id="equals" onClick={calc}>=</button>

                <button id="zero" value={'0'} onClick={buildNumber}>0</button>
                <button id="one" value={'1'} onClick={buildNumber}>1</button>
                <button id="two" value={'2'} onClick={buildNumber}>2</button>
                <button id="three" value={'3'} onClick={buildNumber}>3</button>
                <button id="four" value={'4'} onClick={buildNumber}>4</button>
                <button id="five" value={'5'} onClick={buildNumber}>5</button>
                <button id="six" value={'6'} onClick={buildNumber}>6</button>
                <button id="seven" value={'7'} onClick={buildNumber}>7</button>
                <button id="eight" value={'8'} onClick={buildNumber}>8</button>
                <button id="nine" value={'9'} onClick={buildNumber}>9</button>

                <button id="decimal" value={'.'} onClick={buildNumber}>.</button>
            </div>
        </div>
    )
}
